        cardTitle.className = "mdl-card__title";
        cardDiv.appendChild(cardTitle);

        var titleText = doc.createElement("h2");
        titleText.className = "mdl-card__title-text capitalize";
        titleText.id = "title";
        titleText.innerHTML = color;
        cardTitle.appendChild(titleText);

        var subtitleText1 = doc.createElement("h4");
        subtitleText1.className = "mdl-card__subtitle-text";
        subtitleText1.id = "subtitle-1";
        subtitleText1.innerHTML = "Choose Shade";
        cardTitle.appendChild(subtitleText1);

        var subtitleText2 = doc.createElement("h4");
        subtitleText2.className = "mdl-card__subtitle-text";
        subtitleText2.id = "subtitle-2";
        //subtitleText2.innerHTML = "Subtitle 2";
        cardTitle.appendChild(subtitleText2);

        var subtitleText3 = doc.createElement("h4");
        subtitleText3.className = "mdl-card__subtitle-text";
        subtitleText3.id = "subtitle-3";
        //subtitleText3.innerHTML = "Subtitle 3";
        cardTitle.appendChild(subtitleText3);

        var subtitleText4 = doc.createElement("h4");
        subtitleText4.className = "mdl-card__subtitle-text";
        subtitleText4.id = "subtitle-4";
        //subtitleText4.innerHTML = "Subtitle 4";
        cardTitle.appendChild(subtitleText4);

        var swatchArea = doc.createElement("div");
        swatchArea.className = "mdl-card__media swatch-area mdl-color--white";
        swatchArea.id = "swatch-area";

        if(color === 'brown' || color === 'grey' || color === 'blue-grey'){
            for (var i = 0; i < range.length; i++) {
                var div = doc.createElement("div");
                div.className = "swatch mdl-color--" + color + "-" + range[i];
                swatchArea.appendChild(div);
            }
        } else if (color === 'black'){
                var div = doc.createElement("div");
                var div2 = doc.createElement("div");
                div.className = "swatch mdl-color--black";
                div2.className = "swatch mdl-color--white";
                swatchArea.appendChild(div);
                swatchArea.appendChild(div2);
        } else {
            for (var i = 0; i < rangeExtended.length; i++) {
                var div = doc.createElement("div");
                div.className = "swatch mdl-color--" + color + "-" + rangeExtended[i];
                swatchArea.appendChild(div);
            }            
        }
        cardDiv.appendChild(swatchArea);

        var cardActions = doc.createElement("div");
        cardActions.className = "mdl-card__actions";
        cardDiv.appendChild(cardActions);

        var btn = doc.createElement("button");
        btn.className = "mdl-button mdl-js-button mdl-js-ripple-effect";
        btn.id = "back";
        btn.dataset.page = 2;
        btn.innerHTML = "Back";
        cardActions.appendChild(btn);
        btn.addEventListener("click", back, false);
        attachClickListeners();
        window.componentHandler.upgradeAllRegistered();
    }

    function back(){
        var card = doc.getElementById('card');

        if(this.dataset.page == 2){
            card.parentNode.removeChild(card);
            mainMenu.style.display = "initial";
            window.componentHandler.upgradeAllRegistered();
        }
        if(this.dataset.page == 3){
            var div = doc.getElementById('swatch');
            var cls = div.className;
            var colorName = cls.split("--").pop();
            var color = colorName.replace(/[0-9]/g, '');
            color = color.replace(/[A-Z]/g, '');
            color = color.replace(/-\s*$/, "");

            card.parentNode.removeChild(card);            

            //render black page
            if(color === 'white'){
                color = 'black';
            }

            makeCard(color);
            var btn = doc.getElementById("back");
            btn.dataset.page = 2;
        }
    }
    function rgb2hex(rgb) {
         if (  rgb.search("rgb") == -1 ) {
              return rgb;
         } else {
            rgb = rgb.match(/^rgba?\((\d+),\s*(\d+),\s*(\d+)(?:,\s*(\d+))?\)$/);
            function hex(x) {
               return ("0" + parseInt(x).toString(16)).slice(-2);
            }
            return "#" + hex(rgb[1]) + hex(rgb[2]) + hex(rgb[3]); 
         }
    }
    function insertAfter(newNode, referenceNode) {
        referenceNode.parentNode.insertBefore(newNode, referenceNode.nextSibling);
    }

