			get: function () { 
				var test = 'test';
				try {
					localStorage.setItem(test, test);
					localStorage.removeItem(test);
					return true;
				} catch(e) {
					return false;
				}
			}
		});

		clipboard.on('success', function(e) {
			var existingHTML = e.trigger.innerHTML;
			var alertHTML = existingHTML + '<span id="alert" class="fadeOut">Copied!</span>';
			e.trigger.innerHTML = alertHTML;
			setTimeout(undoAlert,2500);
		});

			//doc.addEventListener("DOMContentLoaded", function(e) {
		window.addEventListener("load", function(e) {
			attachListenersToItems();
			attachListenerToBody();
			doc.querySelector(".page-content").classList.remove("hidden");
				//detect previous preference
			if(ls){
				if(localStorage.getItem('disabled')){
					cbx.checked = false;
				} else {
					cbx.checked = true;
						upgradeItems(); // -------------------------------------------------------------------<<<<<
					}
				} else {
					cbx.checked = true;
					upgradeItems(); // -------------------------------------------------------------------<<<<<
				}
			});		    

		cbx.addEventListener("click", function(){
			if(ls){
				if(cbx.checked){
					localStorage.removeItem('disabled');
					upgradeItems();
				} else {
					localStorage.setItem('disabled', true);
					downgradeItems();
				}
			}
		});		    
		function undoAlert(){
			var alertElement = document.getElementById('alert');
			alertElement.parentNode.removeChild(alertElement);
		}

		function upgradeItems(){
			for (var i = 0; i < items.length; i++) {
				var codeSpan = items[i].getElementsByClassName('code');
				var char = codeSpan[0].innerHTML;
				var str = char;
				var res = str.replace("amp;", "");
				items[i].setAttribute('data-clipboard-text',res);
			}
		}

		function downgradeItems(){
			for (var i = 0; i < items.length; i++) {
				items[i].removeAttribute('data-clipboard-text');
					//items[i].classList.remove('copy');
			}
		}		    
		clipboard.on('error', function(e) {
			console.log(e.trigger,e.action);
		});

		function attachListenersToItems(){
			for (var i = 0; i < items.length; i++) {

				items[i].addEventListener("click", function(e){
					clearAll();
					var node = e.currentTarget;
					var children = node.children;

					header.innerHTML = String(children[1].innerHTML);
					node.classList.add('selected');
					header.classList.add('.header-on-display');

						e = e || window.event // cross-browser event
						if (e.stopPropagation) {
							// W3C standard variant
							e.stopPropagation()
						} else {
							// IE variant
							e.cancelBubble = true
						}						
					});
					// initially apply lift to all buttons
