function domainIncludes(obj,id){
  let result = false;
  let encodedId = "schema:" + id;
  if(obj.hasOwnProperty("schema:domainIncludes")){
    let domainIncludesProp = obj["schema:domainIncludes"];
    if (Array.isArray(domainIncludesProp)){
      for (let i = 0; i < domainIncludesProp.length; i++) {
        if(domainIncludesProp[i]["@id"] === encodedId){
          result = true;
        }
      }
