  let result = false;
  let typeProp = obj["@type"];
  if (Array.isArray(typeProp)){
    if(typeProp.includes("rdf:Property")){
      result = true;
    }
  } else {
    if (typeProp === "rdf:Property"){
      result = true;
    }
  }
  return result;
