function DetailElement(obj) {
    let label = obj["rdfs:label"];
    if(label.hasOwnProperty("@value")){
      label = label["@value"];
    }
    //console.log(label);
    let comment = obj["rdfs:comment"];
    if(comment.hasOwnProperty("@value")){
      comment = comment["@value"];
    }
    comment = comment.replace(/\\n/g, "<br>");
    comment = comment.replace(/<br \/><br \/>/g, "");
    comment = comment.replace(/\r?\n|\r/g, "<br>");
    comment = comment.replace(/\[.*?\]]/g, convertBracketsToAnchors);
    comment = comment.replace(/\[([^\[]+)\](\(([^)]*))\)/gim, '<a href="$3">$1</a>'); // anchor tags
    let summaryText = document.createTextNode(label);
    let thisEncodedLabel = "_" + label;
    let detailsText = document.createTextNode(comment);
    let detailElement = document.createElement("details");
    detailElement.dataset.label = thisEncodedLabel;
    //detailElement.setAttribute("open","")
    let summaryElement = document.createElement("summary");
    summaryElement.appendChild(summaryText);
    detailElement.appendChild(summaryElement);
    //detailElement.appendChild(detailsText);
    detailElement.insertAdjacentHTML('beforeend', comment);
    return detailElement;
}

function nestElements(){
  for (let i = 0; i < classArray.length; i++) {
    let thisTypeId = classArray[i];
    let thisTypeObject = collection.find(item => item["@id"] === thisTypeId);
    if(thisTypeObject.hasOwnProperty("rdfs:subClassOf")){
      let label = thisTypeId.replace("schema:","");
      let thisEncodedLabel = "_" + label;
      let queryString = "details[data-label=" + thisEncodedLabel + "]";
      let thisDetailElement = fragment.querySelector(queryString);
      let subClassOfProperty = thisTypeObject["rdfs:subClassOf"];

      if(Array.isArray(subClassOfProperty)){ // ----------------------if more than one we must clone then paste to each
        for (let ii = 0; ii < subClassOfProperty.length; ii++) {
          let thisDetailElementClone = thisDetailElement.cloneNode(true);
          cutPasteDetail(thisDetailElementClone, subClassOfProperty[ii]);
        }
        thisDetailElement.remove();
      } else { //---------------------------------------------if just one we can simply appendChild to move it
        cutPasteDetail(thisDetailElement, subClassOfProperty);
      }

    }
  }
  //now we need to make another pass and move DataTypes to DataType
      // "@type": [
      //   "schema:DataType",
      //   "rdfs:Class"
      // ],
  let dataTypeDetailElement = fragment.querySelector("details[data-label='_DataType']");
  for (let i = 0; i < classArray.length; i++) {
    let thisId = classArray[i];
    let thisObject = collection.find(item => item["@id"] === thisId);
    if(thisObject){
      if(thisObject.hasOwnProperty("@type")){
        if(thisObject["@type"].includes("schema:DataType")){
          let label = thisId.replace("schema:","");
          let thisEncodedLabel = "_" + label;
          let queryString = "details[data-label=" + thisEncodedLabel + "]";
          let thisDetailElement = fragment.querySelector(queryString);
          if(thisDetailElement){
            dataTypeDetailElement.appendChild(thisDetailElement);
          }
        }        
      }
    }
  }
}
function cutPasteDetail(childElement, subClassOfProperty){
  if(childElement.dataset.label !== "_DataType"){
    let subClassOfPropertyValue = subClassOfProperty["@id"];
    if(subClassOfPropertyValue){
      subClassOfPropertyValue = subClassOfPropertyValue.split(":").pop();
      let parentEncodedLabel = "_" + subClassOfPropertyValue;
      let parentQueryString = "details[data-label=" + parentEncodedLabel + "]";
      let parentDetailElement = fragment.querySelector(parentQueryString);
      parentDetailElement.appendChild(childElement);
    }          
  }
}
